namespace WeatherToText
{
    // Directives
    using System;
    using System.Collections.Generic;
    using System.IO;
    using System.Net;
    using System.Text;
    using System.Text.RegularExpressions;
    using System.Xml.XPath;
    using ConsoleTableExt;
    using HtmlAgilityPack;

    /// <summary>
    /// Main class.
    /// </summary>
    class MainClass
    {
        /// <summary>
        /// The regex.
        /// </summary>
        private static readonly Regex regex = new Regex(@"Â|\s+|\(\d+\)", RegexOptions.Compiled);

        /// <summary>
        /// The entry point of the program, where the program control starts and ends.
        /// </summary>
        /// <param name="args">The command-line arguments.</param>
        private static void Main(string[] args)
        {
            // Set encoding
            Console.OutputEncoding = System.Text.Encoding.UTF8;

            // Set console foreground color
            var consoleForegoundColor = Console.ForegroundColor;

            // Check for no args
            if (args.Length == 0)
            {
                // Advice user
                Console.WriteLine("Missing arguments.");

                // Halt program flow
                return;
            }

            // Declare variables
            var nextDay = false;
            var nextDayHour = -1;

            // Catch errors
            try
            {
                // Check for a color argument
                if (args.Length > 1)
                {
                    // Set passed color ensuring first letter's uppercase 
                    string passedColor = $"{char.ToUpperInvariant(args[1][0])}{args[1].Substring(1)}";

                    // Set text color for program output
                    Console.ForegroundColor = (ConsoleColor)Enum.Parse(typeof(ConsoleColor), passedColor);
                }

                // Check for new day
                if (args.Length > 2 && args[2] == "/N")
                {
                    // Set new day flag
                    nextDay = true;
                }

                // Check for hour of new day
                if (args.Length > 3 && args[3] == "/T")
                {
                    // Declare hour of the day
                    int dayHour;

                    // Check for a numeric hour
                    if (int.TryParse(args[4], out dayHour))
                    {
                        // Set new day hour
                        nextDayHour = dayHour;
                    }
                }

                // Toggle next day by date
                if (nextDayHour > -1)
                {
                    // Compare against current  hour
                    if (int.Parse($"{DateTime.Now.Hour}{DateTime.Now.Minute}") < nextDayHour)
                    {
                        // Set new day to false
                        nextDay = false;
                    }
                }

                // Configure service point manager
                ServicePointManager.Expect100Continue = true;
                ServicePointManager.SecurityProtocol = SecurityProtocolType.Tls12 | SecurityProtocolType.Ssl3 | SecurityProtocolType.Tls | SecurityProtocolType.Tls11;

                // Set web client
                WebClient webClient = new WebClient();

                // Declare the html string
                string htmlString = string.Empty;

                // Check for local or remote fetch
                if (args[0].StartsWith("http"))
                {
                    // Set html string by download
                    htmlString = webClient.DownloadString(args[0]);
                }
                else
                {
                    // Set html string by local file
                    htmlString = File.ReadAllText(args[0]);
                }

                // Set empty output file path
                string outputFilePath = string.Empty;

                // Check for /O:<Path to file>
                foreach (string arg in args)
                {
                    if (arg.ToLowerInvariant().StartsWith("/o:"))
                    {
                        outputFilePath = arg.Split(new char[] { ':' }, 2)[1];
                    }
                }

                // The HTML document
                HtmlDocument htmlDocument = new HtmlDocument();

                // Load the HTML
                htmlDocument.LoadHtml(htmlString);

                StringBuilder stringBuilder = new StringBuilder();

                // Print today
                stringBuilder.AppendLine(PrintTable(htmlDocument, false));

                // Print tomorrow
                if (nextDay)
                {
                    // Spacer
                    stringBuilder.AppendLine();

                    // Print tomorrow
                    stringBuilder.AppendLine(PrintTable(htmlDocument, true));
                }

                // Write to output file
                if (outputFilePath != string.Empty)
                {
                    File.WriteAllText(outputFilePath, stringBuilder.ToString());
                }

                // Write to console
                Console.Write(stringBuilder.ToString());
            }
            catch (Exception exception)
            {
                // Report error
                Console.WriteLine($"Error: {exception.Message}");
            }
            finally
            {
                // Set console color back
                if (args.Length > 1)
                {
                    // Set foregronud color
                    Console.ForegroundColor = consoleForegoundColor;
                }
            }
        }

        /// <summary>
        /// Prints the table.
        /// </summary>
        /// <returns>The table.</returns>
        /// <param name="htmlDocument">Html document.</param>
        /// <param name="nextDay">If set to <c>true</c> next day.</param>
        private static string PrintTable(HtmlDocument htmlDocument, bool nextDay)
        {
            // The exported table string
            string exportedTable = string.Empty;

            // The table data list
            List<List<object>> tableDataList = new List<List<object>>();

            // Insert first-row substitutions
            var firstRowDataList = new List<object>();
            firstRowDataList.Add("Tid");
            firstRowDataList.Add("temp");
            firstRowDataList.Add("känns");
            firstRowDataList.Add("mm");
            firstRowDataList.Add("m/s");
            firstRowDataList.Add($"nederbörd  ");
            firstRowDataList.Add($"åska");

            // Add first row
            tableDataList.Add(firstRowDataList);

            // Set rows
            HtmlNodeCollection rows = htmlDocument.DocumentNode.SelectNodes("//article[@class='day']")[nextDay ? 1 : 0].SelectNodes($".//*[starts-with(@id, 'hour-{(nextDay ? 2 : 1)}_')]");

            // Process rows
            foreach (HtmlNode rowHtmlNode in rows)
            {
                // Declare row data list
                var rowDataList = new List<object>();

                // Insert time
                rowDataList.Add(rowHtmlNode.SelectSingleNode(".//time").InnerText.Trim());

                // Set columns 
                HtmlNodeCollection values = rowHtmlNode.SelectNodes(".//span[@class='value']");

                // Iterate cells
                for (int valueIndex = 0; valueIndex < 6; valueIndex++)
                {
                    // Set processed inner text
                    string value = regex.Replace(values[valueIndex].InnerText.Trim(), string.Empty);

                    // Check length
                    if (value.Length > 0)
                    {
                        // Has length and it's within working range. Add it
                        rowDataList.Add(value);
                    }
                }

                // Push row into table data
                tableDataList.Add(rowDataList);
            }

            // Check if must display
            if (tableDataList.Count > 0)
            {
                // Set exported table 
                exportedTable =
                $"                        Sannolikhet Sannolikhet{Environment.NewLine}{exportedTable}" +
                ConsoleTableBuilder.From(tableDataList)
               .WithFormat(ConsoleTableBuilderFormat.Minimal)
               .Export().ToString(); ;
            }

            return exportedTable;
        }
    }
}
